import { SIGNIN } from '../actions';

const INITIAL_STATE = {
  email: '',
};

export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
  case SIGNIN:
    return { ...state, email: action.email };
  default:
    return state;
  }
}
